import React from 'react'
import '../App.css'
import memeData from '../memeData'

const Form = (props) => {

    const [meme, setMeme] = React.useState({
        topText: '',
        bottonTxt: '',
        randomImg: 'http://i.imgflip.com/1bij.jpg'
    })

    const [allMemeImages, setAllMemeImage] = React.useState(memeData)


    function getMeme(){
        const memeArr = allMemeImages.data.memes
        const randomMeme = Math.floor(Math.random() * memeArr.length)
        const url = memeArr[randomMeme].url
        setMeme(preMeme => ({
            ...preMeme,
            randomImg: url
        }))
    }

    function setText(event){
        const{name, value} = event.target
        setAllMemeImage(prevImg =>({
            ...prevImg,
            [name]: value
        }))
    }

  return (
    <main>
        <div className='form' action="POST">
                <input className='inText' type="text" placeholder='Top text' name='topText' value={meme.topText} onChange={setText}/>
                <input className='inText' type="text" placeholder='Bottom text' name='bottonTxt' value={meme.bottonTxt} onChange={setText} />
                <button onClick={getMeme} className='subBtn' >Get a new meme image ðŸ–¼</button>
        </div>
        <div className="meme">
                <img src={meme.randomImg} className="meme--image" alt='img'/>
                <h2 className="meme--text top">{meme.topText}</h2>
                <h2 className="meme--text bottom">{meme.bottonTxt}</h2>
        </div>

    </main>
  )
}

export default Form
